import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, ImageOverlay, GeoJSON } from 'react-leaflet';
import axios from 'axios';
import * as turf from '@turf/turf';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';
import './styles.css';

// Fix Leaflet marker icons
import L from 'leaflet';
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: icon,
  shadowUrl: iconShadow
});

const layerStyles = {
  waterBoundary: { color: '#00f', weight: 2, fillOpacity: 0 },
  studyArea: { color: '#ff0000', weight: 2 },
  changeErosion: { 
    color: '#ff0000', 
    weight: 3,
    dashArray: '5,5',
    fillColor: '#ff0000',
    fillOpacity: 0.3
  },
  changeAccretion: {
    color: '#00ff00',
    weight: 3,
    dashArray: '5,5',
    fillColor: '#00ff00',
    fillOpacity: 0.3
  }
};

const MapComponent = () => {
  const [mapData, setMapData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeLayer, setActiveLayer] = useState('change');
  const [selectedPeriod, setSelectedPeriod] = useState('both');

  const handleDateSelect = async (startDate, endDate) => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/get-map-data', {
        start_date: startDate,
        end_date: endDate
      });

      if (response.data.error) throw new Error(response.data.error);
      
      setMapData({
        ...response.data,
        bounds: new L.LatLngBounds(
          new L.LatLng(46.0, -64.0),  // Adjust these coordinates
          new L.LatLng(47.0, -63.0)    // to match your study area
        )
      });
      
    } catch (error) {
      console.error('Error loading data:', error);
      setError(error.response?.data?.error || error.message);
    } finally {
      setLoading(false);
    }
  };

  const simplifyGeometry = (geometry) => {
    try {
      return turf.simplify(turf.feature(geometry), {
        tolerance: 0.001,
        highQuality: true
      }).geometry;
    } catch (e) {
      console.error('Geometry simplification failed:', e);
      return geometry;
    }
  };

  const renderChangeAnalysis = () => {
    if (!mapData) return null;

    return (
      <>
        <GeoJSON
          data={mapData.baseline_geojson}
          style={{ ...layerStyles.waterBoundary, color: '#0000ff' }}
        />

        {selectedPeriod !== 'baseline' && mapData.water_boundaries?.comparison?.features?.map((feature, index) => (
          <GeoJSON
            key={`comp-${index}`}
            data={simplifyGeometry(feature.geometry)}
            style={{ ...layerStyles.waterBoundary, color: '#00ff00' }}
          />
        ))}

        {mapData.water_boundaries?.erosion?.features?.map((feature, index) => (
          <GeoJSON
            key={`ero-${index}`}
            data={simplifyGeometry(feature.geometry)}
            style={layerStyles.changeErosion}
          />
        ))}

        {mapData.water_boundaries?.accretion?.features?.map((feature, index) => (
          <GeoJSON
            key={`acc-${index}`}
            data={simplifyGeometry(feature.geometry)}
            style={layerStyles.changeAccretion}
          />
        ))}
      </>
    );
  };

  return (
    <div className="map-container">
      <MapContainer 
        center={[46.5107, -63.4168]}
        zoom={9}
        style={{ height: '100vh', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />

        {activeLayer === 'rgb' && mapData?.comparison_image && (
          <ImageOverlay
            url={mapData.comparison_image}
            bounds={mapData.bounds}
            opacity={0.8}
          />
        )}

        {activeLayer === 'change' && renderChangeAnalysis()}

        <div className="change-legend">
          <h4>Legend</h4>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#0000ff' }} />
            <span>2001 Baseline</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#00ff00' }} />
            <span>Current Coastline</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#ff0000' }} />
            <span>Erosion</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#00ff00' }} />
            <span>Accretion</span>
          </div>
        </div>
      </MapContainer>

      <div className="sidebar">
        <div className="sidebar-header">
          <h3>Coastal Change Analyzer</h3>
          <DatePicker onDateSelect={handleDateSelect} />
        </div>
        
        <div className="controls">
          <div className="time-period-selector">
            <button className={selectedPeriod === 'both' ? 'active' : ''}
              onClick={() => setSelectedPeriod('both')}>Both</button>
            <button className={selectedPeriod === 'baseline' ? 'active' : ''}
              onClick={() => setSelectedPeriod('baseline')}>2001</button>
            <button className={selectedPeriod === 'comparison' ? 'active' : ''}
              onClick={() => setSelectedPeriod('comparison')}>Current</button>
          </div>

          <div className="view-toggle">
            <button className={activeLayer === 'change' ? 'active' : ''}
              onClick={() => setActiveLayer('change')}>Change Analysis</button>
            <button className={activeLayer === 'rgb' ? 'active' : ''}
              onClick={() => setActiveLayer('rgb')}>Satellite View</button>
          </div>

          {mapData && (
            <button className="download-btn" onClick={handleDownloadData}>
              Export Data
            </button>
          )}
        </div>
      </div>

      {loading && (
        <div className="loading-overlay">
          <div className="spinner"></div>
          Analyzing changes...
        </div>
      )}
      {error && <div className="error-overlay">{error}</div>}
    </div>
  );
};

export default MapComponent;
