import React, { useState } from 'react';

const DatePicker = ({ onDateSelect }) => {
    const [startDate, setStartDate] = useState('2023-06-01');
    const [endDate, setEndDate] = useState('2023-06-30');
    
    // Fixed baseline period details
    const baselineInfo = {
        start: '2016-09-01',
        end: '2017-09-30',
        satellite: 'Sentinel-2A'
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Pass comparison dates only (baseline is fixed)
        onDateSelect(startDate, endDate);
    };

    return (
        <div className="date-picker-overlay">
            <div className="date-picker-modal">
                <h3>Coastline Change Analysis</h3>
                <div className="baseline-info">
                    <h4>Baseline Period (Fixed)</h4>
                    <p>
                        {baselineInfo.start} to {baselineInfo.end}<br />
                        <em>({baselineInfo.satellite} imagery)</em>
                    </p>
                </div>

                <form onSubmit={handleSubmit}>
                    <div className="period-section">
                        <h4>Comparison Period</h4>
                        <div className="date-input-group">
                            <label>Start Date:</label>
                            <input 
                                type="date" 
                                value={startDate}
                                onChange={(e) => setStartDate(e.target.value)}
                                min="2017-01-01"  // After baseline period
                                max={new Date().toISOString().split('T')[0]}
                            />
                        </div>
                        <div className="date-input-group">
                            <label>End Date:</label>
                            <input 
                                type="date" 
                                value={endDate}
                                onChange={(e) => setEndDate(e.target.value)}
                                min={startDate}
                                max={new Date().toISOString().split('T')[0]}
                            />
                        </div>
                    </div>

                    <div className="analysis-info">
                        <p>
                            The analysis will compare your selected period against<br />
                            the baseline ({baselineInfo.start} to {baselineInfo.end})<br />
                            using Sentinel-2 satellite imagery.
                        </p>
                    </div>

                    <button type="submit" className="analyze-button">
                        Run Coastal Change Analysis
                    </button>
                </form>
            </div>
        </div>
    );
};

export default DatePicker;
