import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, GeoJSON, ImageOverlay } from 'react-leaflet';
import axios from 'axios';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';
import './styles.css';

const MapComponent = () => {
    const [mapData, setMapData] = useState({
        rgbThumbnail: null,
        shoreline: null,
        bounds: null
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showBaseline, setShowBaseline] = useState(true);

    const handleDateSelect = async (endDate) => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.post('http://localhost:5000/get-map-data', {
                end_date: endDate
            });
            
            setMapData({
                rgbThumbnail: response.data.rgb_thumbnail,
                shoreline: response.data.shoreline,
                bounds: response.data.bounds
            });

        } catch (error) {
            setError(error.response?.data?.error || "Analysis failed. Try dates between 2017-2023");
        } finally {
            setLoading(false);
        }
    };

    const handleDownload = async () => {
        try {
            const response = await axios.post('http://localhost:5000/download-shoreline', {
                features: mapData.shoreline.features
            });
            
            // Create download link
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'shoreline.zip');
            document.body.appendChild(link);
            link.click();
            link.remove();

        } catch (error) {
            console.error('Download failed:', error);
        }
    };

    return (
        <div className="map-container">
            <MapContainer 
                center={[46.5107, -63.4168]}
                zoom={10}
                style={{ height: '100vh', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                />

                {mapData.rgbThumbnail && (
                    <ImageOverlay 
                        url={mapData.rgbThumbnail}
                        bounds={mapData.bounds}
                        opacity={0.8}
                    />
                )}

                {mapData.shoreline && (
                    <GeoJSON 
                        data={mapData.shoreline}
                        style={{ color: '#00f', weight: 2 }}
                    />
                )}

                <div className="control-panel">
                    <div className="control-section">
                        <h3>Coastal Analysis Controls</h3>
                        
                        <DatePicker onDateSelect={handleDateSelect} />
                        
                        {mapData.shoreline && (
                            <button 
                                onClick={handleDownload}
                                className="download-button"
                            >
                                Download Shoreline Shapefile
                            </button>
                        )}

                        {loading && (
                            <div className="loading-state">
                                <div className="spinner"></div>
                                Processing Satellite Imagery
                            </div>
                        )}

                        {error && (
                            <div className="error-state">
                                ⚠️ Error: {error}
                            </div>
                        )}
                    </div>
                </div>
            </MapContainer>
        </div>
    );
};

export default MapComponent;
