import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, GeoJSON, ImageOverlay } from 'react-leaflet';
import axios from 'axios';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
    const [layers, setLayers] = useState({
        rgb: null,
        mask: null,
        shoreline: null
    });
    const [aoiBounds, setAoiBounds] = useState(null);

    const handleAnalyze = async (endDate) => {
        try {
            const response = await axios.post('http://localhost:5000/get-analysis', {
                end_date: endDate
            });
            
            const bounds = response.data.bounds[0];
            const leafletBounds = [
                [bounds[0][1], bounds[0][0]], 
                [bounds[2][1], bounds[2][0]]
            ];
            
            setLayers({
                rgb: response.data.rgb_image,
                mask: response.data.water_mask,
                shoreline: response.data.shoreline
            });
            setAoiBounds(leafletBounds);
            
        } catch (error) {
            console.error('Analysis failed:', error);
        }
    };

    return (
        <MapContainer center={[46.5107, -63.4168]} zoom={10} style={{ height: '100vh' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
            
            {aoiBounds && (
                <>
                    <ImageOverlay 
                        url={layers.rgb} 
                        bounds={aoiBounds}
                        opacity={0.8}
                    />
                    <ImageOverlay
                        url={layers.mask}
                        bounds={aoiBounds}
                        opacity={0.4}
                    />
                    <GeoJSON
                        data={layers.shoreline}
                        style={{ color: 'blue', weight: 2 }}
                    />
                </>
            )}
            
            <div className="control-panel">
                <DatePicker onDateSelect={handleAnalyze} />
                <div className="legend">
                    <h4>Water Mask Legend</h4>
                    <div className="legend-item">
                        <div style={{ background: '#0000FF' }}></div>
                        <span>Water</span>
                    </div>
                </div>
            </div>
        </MapContainer>
    );
};

export default MapComponent;
