import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, GeoJSON, ImageOverlay } from 'react-leaflet';
import axios from 'axios';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';
import './styles.css';

const MapComponent = () => {
    const [mapData, setMapData] = useState({
        rgbThumbnail: null,
        baseline: null,
        current: null,
        erosion: null,
        accretion: null,
        ndwiParams: null
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showBaseline, setShowBaseline] = useState(true);
    const [showRGB, setShowRGB] = useState(true);

    const layerStyles = {
        baseline: { color: '#ff0000', weight: 2 },
        current: { color: '#0000ff', weight: 2 },
        erosion: { color: '#ff0000', weight: 3, dashArray: '5,5' },
        accretion: { color: '#00ff00', weight: 3, dashArray: '5,5' }
    };

    useEffect(() => {
        const loadBaseline = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get-baseline');
                setMapData(prev => ({
                    ...prev,
                    baseline: response.data
                }));
            } catch (error) {
                console.error('Error loading baseline:', error);
                setError("Failed to load baseline coastline data");
            }
        };
        loadBaseline();
    }, []);

    const handleDateSelect = async (endDate) => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.post('http://localhost:5000/get-map-data', {
                end_date: endDate
            });

            setMapData({
                ...response.data,
                baseline: mapData.baseline // Preserve existing baseline
            });

        } catch (error) {
            setError(error.response?.data?.error || "Analysis failed. Try dates between 2017-2023");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="map-container">
            <MapContainer 
                center={[46.5107, -63.4168]}
                zoom={10}
                style={{ height: '100vh', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                />

                {/* RGB Image Overlay */}
                {showRGB && mapData.rgbThumbnail && (
                    <ImageOverlay 
                        url={mapData.rgbThumbnail}
                        bounds={[[46.0, -64.5], [47.0, -62.5]]}
                        opacity={0.8}
                    />
                )}

                {/* Baseline Coastline */}
                {showBaseline && mapData.baseline && (
                    <GeoJSON 
                        data={mapData.baseline} 
                        style={layerStyles.baseline}
                        onEachFeature={(feature, layer) => {
                            layer.bindTooltip("PEI Baseline Coastline (2000)");
                        }}
                    />
                )}

                {/* Current Shoreline */}
                {mapData.current && <GeoJSON data={mapData.current} style={layerStyles.current} />}
                
                {/* Changes */}
                {mapData.erosion && <GeoJSON data={mapData.erosion} style={layerStyles.erosion} />}
                {mapData.accretion && <GeoJSON data={mapData.accretion} style={layerStyles.accretion} />}

                {/* Legend */}
                <div className="change-legend">
                    <div className="legend-item">
                        <div className="legend-color" style={{ backgroundColor: '#ff0000' }}></div>
                        <span>Baseline (2000)</span>
                    </div>
                    <div className="legend-item">
                        <div className="legend-color" style={{ backgroundColor: '#0000ff' }}></div>
                        <span>Current Shoreline</span>
                    </div>
                    <div className="legend-item">
                        <div className="legend-color" style={{ 
                            border: '2px dashed #ff0000',
                            background: 'transparent'
                        }}></div>
                        <span>Erosion Areas</span>
                    </div>
                    <div className="legend-item">
                        <div className="legend-color" style={{ 
                            border: '2px dashed #00ff00',
                            background: 'transparent'
                        }}></div>
                        <span>Accretion Areas</span>
                    </div>
                </div>
            </MapContainer>

            <div className="control-panel right-side-controls">
                <div className="control-section">
                    <div className="control-header">
                        <h3>PEI Coastal Analysis</h3>
                        <div className="baseline-toggle">
                            <label>
                                <input 
                                    type="checkbox"
                                    checked={showBaseline}
                                    onChange={(e) => setShowBaseline(e.target.checked)}
                                />
                                Show 2000 Baseline
                            </label>
                        </div>
                        <div className="rgb-toggle">
                            <label>
                                <input 
                                    type="checkbox"
                                    checked={showRGB}
                                    onChange={(e) => setShowRGB(e.target.checked)}
                                />
                                Show Satellite Imagery
                            </label>
                        </div>
                    </div>

                    <div className="date-selection-card">
                        <h4>Select Analysis Date</h4>
                        <DatePicker onDateSelect={handleDateSelect} />
                    </div>

                    {loading && (
                        <div className="loading-state">
                            <div className="spinner"></div>
                            <div className="loading-text">
                                <p>Processing Satellite Imagery</p>
                                <small>Steps in progress:</small>
                                <ul>
                                    <li>Loading RGB composite</li>
                                    <li>Calculating NDWI</li>
                                    <li>Extracting shoreline</li>
                                </ul>
                            </div>
                        </div>
                    )}

                    {error && (
                        <div className="error-state">
                            ⚠️ Error: {error}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default MapComponent;
