import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, ImageOverlay } from "react-leaflet";
import { getMapData } from "./api";
import "leaflet/dist/leaflet.css";
import "./styles.css";
import L from 'leaflet';

const PEI_CENTER = [46.5107, -63.4168]; // Central PEI coordinates
const DEFAULT_ZOOM = 9;

const MapComponent = () => {
    const [mapData, setMapData] = useState(null);
    const [mapInstance, setMapInstance] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            const data = await getMapData();
            if (data) {
                setMapData(data);
                // Adjust map view when data loads
                if (mapInstance) {
                    const bounds = L.latLngBounds(data.bounds);
                    mapInstance.fitBounds(bounds, {
                        padding: [50, 50], // Optional padding
                        maxZoom: 12
                    });
                }
            }
        };
        fetchData();
    }, [mapInstance]);

    return (
        <div className="map-container">
            <h2>PEI Coastal Analysis</h2>
            <MapContainer 
                center={PEI_CENTER}
                zoom={DEFAULT_ZOOM}
                style={{ height: "600px", width: "100%" }}
                whenCreated={setMapInstance}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                
                {mapData && (
                    <ImageOverlay
                        url={mapData.image_url}
                        bounds={mapData.bounds}
                        opacity={0.7}
                        attribution="Google Earth Engine | Sentinel-2"
                    />
                )}
            </MapContainer>
        </div>
    );
};

export default MapComponent;
