import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, GeoJSON, ImageOverlay } from 'react-leaflet';
import axios from 'axios';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';
import './styles.css';

const MapComponent = () => {
    const [analysisData, setAnalysisData] = useState({
        aoi: null,
        thumbnail: null,
        waterMask: null,
        bounds: null
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Load AOI on mount
    useEffect(() => {
        const loadAOI = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get-aoi');
                setAnalysisData(prev => ({
                    ...prev,
                    aoi: response.data
                }));
            } catch (error) {
                console.error('Error loading AOI:', error);
            }
        };
        loadAOI();
    }, []);

    const handleAnalyze = async (endDate) => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.post('http://localhost:5000/get-analysis', {
                end_date: endDate
            });

            setAnalysisData({
                aoi: analysisData.aoi, // Preserve AOI
                thumbnail: response.data.thumbnail,
                waterMask: response.data.water_mask,
                bounds: response.data.bounds
            });

        } catch (error) {
            setError(error.response?.data?.error || "Analysis failed");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="map-container">
            <MapContainer 
                center={[46.5107, -63.4168]}
                zoom={10}
                style={{ height: '100vh', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                />

                {/* AOI Base Layer */}
                {analysisData.aoi && (
                    <GeoJSON 
                        data={analysisData.aoi}
                        style={{
                            color: '#0044ff',
                            weight: 3,
                            fillColor: '#00ffff',
                            fillOpacity: 0.1
                        }}
                        onEachFeature={(feature, layer) => {
                            layer.bindTooltip("PEI Analysis Boundary");
                        }}
                    />
                )}

                {/* Cropped Satellite Imagery */}
                {analysisData.thumbnail && analysisData.bounds && (
                    <ImageOverlay 
                        url={analysisData.thumbnail}
                        bounds={analysisData.bounds}
                        opacity={0.9}
                    />
                )}

                {/* Water Mask */}
                {analysisData.waterMask && (
                    <GeoJSON 
                        data={analysisData.waterMask}
                        style={{
                            color: '#0000ff',
                            weight: 1,
                            fillColor: '#0000ff',
                            fillOpacity: 0.3
                        }}
                    />
                )}

                {/* Control Panel */}
                <div className="control-panel">
                    <div className="control-section">
                        <h3>Coastal Analysis</h3>
                        <DatePicker onDateSelect={handleAnalyze} />
                        
                        {loading && (
                            <div className="loading-state">
                                <div className="spinner"></div>
                                Processing...
                            </div>
                        )}

                        {error && (
                            <div className="error-state">
                                ⚠️ Error: {error}
                            </div>
                        )}
                    </div>
                </div>
            </MapContainer>
        </div>
    );
};

export default MapComponent;
