import React, { useState } from 'react';

const DatePicker = ({ onDateSelect }) => {
    const [startDate, setStartDate] = useState('2017-06-01');
    const [endDate, setEndDate] = useState('2023-06-30');

    // Format date for display MM/DD/YYYY
    const formatDisplayDate = (dateString) => {
        const [year, month, day] = dateString.split('-');
        return `${month.padStart(2, '0')}/${day.padStart(2, '0')}/${year}`;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onDateSelect(startDate, endDate);
    };

    return (
        <div className="date-picker-overlay">
            <div className="date-picker-modal">
                <h3>Coastline Change Analysis</h3>
                <form onSubmit={handleSubmit}>
                    <div className="period-section">
                        <h4>Select Analysis Period</h4>
                        <div className="date-display">
                            <p>Start Date: {formatDisplayDate(startDate)}</p>
                            <p>End Date: {formatDisplayDate(endDate)}</p>
                        </div>
                        <div className="date-input-group">
                            <label>Start Date:</label>
                            <input 
                                type="date" 
                                value={startDate}
                                onChange={(e) => setStartDate(e.target.value)}
                                min="2017-01-01"
                                max={endDate}
                            />
                        </div>
                        <div className="date-input-group">
                            <label>End Date:</label>
                            <input 
                                type="date" 
                                value={endDate}
                                onChange={(e) => setEndDate(e.target.value)}
                                min={startDate}
                                max={new Date().toISOString().split('T')[0]}
                            />
                        </div>
                    </div>
                    <button type="submit" className="analyze-button">
                        Analyze Coastal Changes
                    </button>
                </form>
            </div>
        </div>
    );
};

export default DatePicker;
