import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, ImageOverlay, GeoJSON } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import * as turf from '@turf/turf';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';
import './styles.css';

const layerStyles = {
  waterBoundary: {
    color: '#00f',
    weight: 2,
    fillOpacity: 0
  },
  studyArea: {
    color: '#ff0000',
    weight: 2
  }
};

const MapComponent = () => {
  const [mapInstance, setMapInstance] = useState(null);
  const [mapData, setMapData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeLayer, setActiveLayer] = useState('rgb');

  const handleDateSelect = async (startDate, endDate) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('http://localhost:5000/get-map-data', {
        start_date: startDate,
        end_date: endDate
      });

      if (response.data.error) throw new Error(response.data.error);
      setMapData(response.data);
    } catch (error) {
      console.error('Error loading data:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadShapefile = async () => {
    try {
      const response = await axios.post('http://localhost:5000/download-shapefile', {
        geojson: mapData.water_boundary
      }, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'water_boundaries.zip');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Download failed:', error);
      setError('Failed to download shapefile');
    }
  };

  useEffect(() => {
    if (mapInstance && mapData?.bounds) {
      const bounds = L.latLngBounds(mapData.bounds);
      mapInstance.fitBounds(bounds, { padding: [50, 50] });
    }
  }, [mapInstance, mapData]);

  const simplifyGeometry = (geometry) => {
    try {
      return turf.simplify(turf.feature(geometry), {
        tolerance: 0.001,
        highQuality: true
      }).geometry;
    } catch (e) {
      console.error('Geometry simplification failed:', e);
      return geometry;
    }
  };

  return (
    <div className="map-container">
      <MapContainer 
        center={[46.5107, -63.4168]}
        zoom={9}
        style={{ height: '600px', width: '100%' }}
        whenCreated={setMapInstance}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />

        {activeLayer === 'rgb' && mapData?.image_url && (
          <ImageOverlay
            url={mapData.image_url}
            bounds={mapData.bounds}
            opacity={0.8}
          />
        )}

        {activeLayer === 'ndwi' && mapData?.ndwi_url && (
          <ImageOverlay
            url={mapData.ndwi_url}
            bounds={mapData.bounds}
            opacity={0.8}
          />
        )}

        {activeLayer === 'boundaries' && mapData?.water_boundary?.features?.map((feature, index) => (
          <GeoJSON
            key={index}
            data={simplifyGeometry(feature.geometry)}
            style={layerStyles.waterBoundary}
          />
        ))}

        <GeoJSON 
          data={mapData?.geojson}
          style={layerStyles.studyArea}
        />
      </MapContainer>

      <div className="sidebar right-sidebar">
        <div className="sidebar-header">
          <h3>Layer Controls</h3>
          <DatePicker onDateSelect={handleDateSelect} />
        </div>

        <div className="layer-controls">
          <button 
            className={activeLayer === 'rgb' ? 'active' : ''}
            onClick={() => setActiveLayer('rgb')}
          >
            RGB Imagery
          </button>
          
          <button
            className={activeLayer === 'ndwi' ? 'active' : ''}
            onClick={() => setActiveLayer('ndwi')}
          >
            NDWI Analysis
          </button>

          <button
            className={activeLayer === 'boundaries' ? 'active' : ''}
            onClick={() => setActiveLayer('boundaries')}
          >
            Water Boundaries
          </button>

          {mapData?.water_boundary && (
            <button
              className="download-btn"
              onClick={handleDownloadShapefile}
            >
              Download Shapefile
            </button>
          )}
        </div>
      </div>

      {loading && <div className="loading-overlay">Loading analysis...</div>}
      {error && <div className="error-overlay">{error}</div>}
    </div>
  );
};

export default MapComponent;
