import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, GeoJSON, ImageOverlay } from 'react-leaflet';
import axios from 'axios';
import DatePicker from './DatePicker';
import 'leaflet/dist/leaflet.css';
import './styles.css';

const MapComponent = () => {
    const [mapData, setMapData] = useState({
        aoi: null,
        rgbThumbnail: null,
        shoreline: null,
        bounds: null
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Load AOI shapefile on mount
    useEffect(() => {
        const loadAOI = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get-aoi');
                setMapData(prev => ({
                    ...prev,
                    aoi: response.data
                }));
            } catch (error) {
                console.error('Error loading AOI:', error);
            }
        };
        loadAOI();
    }, []);

    const handleDateSelect = async (endDate) => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.post('http://localhost:5000/get-analysis', {
                end_date: endDate
            });

            setMapData({
                aoi: mapData.aoi,  // Preserve AOI
                ...response.data
            });

        } catch (error) {
            setError(error.response?.data?.error || "Analysis failed. Check date selection");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="map-container">
            <MapContainer 
                bounds={mapData.bounds}
                style={{ height: '100vh', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                />

                {/* AOI Base Layer */}
                {mapData.aoi && (
                    <GeoJSON 
                        data={mapData.aoi}
                        style={{ 
                            color: '#FFA500',
                            weight: 3,
                            fillOpacity: 0.1
                        }}
                        onEachFeature={(feature, layer) => {
                            layer.bindTooltip("PEI Analysis Boundary");
                        }}
                    />
                )}

                {/* Clipped Satellite Imagery */}
                {mapData.rgbThumbnail && mapData.bounds && (
                    <ImageOverlay 
                        url={mapData.rgbThumbnail}
                        bounds={mapData.bounds}
                        opacity={0.9}
                        zIndex={1}
                    />
                )}

                {/* Water Mask Shoreline */}
                {mapData.shoreline && (
                    <GeoJSON 
                        data={mapData.shoreline}
                        style={{
                            color: '#0066cc',
                            weight: 2,
                            fillColor: '#00a8ff',
                            fillOpacity: 0.4
                        }}
                        zIndex={2}
                    />
                )}

                <div className="control-panel">
                    <div className="control-section">
                        <h3>PEI Coastal Analysis</h3>
                        <DatePicker onDateSelect={handleDateSelect} />
                        
                        {loading && (
                            <div className="loading-state">
                                <div className="spinner"></div>
                                Processing Clipped Imagery
                            </div>
                        )}

                        {error && (
                            <div className="error-state">
                                ⚠️ Error: {error}
                            </div>
                        )}
                    </div>
                </div>
            </MapContainer>
        </div>
    );
};

export default MapComponent;
