import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, ImageOverlay, GeoJSON } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './styles.css';

const MapComponent = ({ mapData }) => {
    const [mapInstance, setMapInstance] = useState(null);

    useEffect(() => {
        if (mapInstance && mapData?.bounds) {
            const bounds = L.latLngBounds(mapData.bounds);
            mapInstance.fitBounds(bounds, { padding: [50, 50] });
        }
    }, [mapInstance, mapData]);

    return (
        <div className="map-container">
            <MapContainer 
                center={[46.5107, -63.4168]}
                zoom={9}
                style={{ height: '600px', width: '100%' }}
                whenCreated={setMapInstance}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors'
                />
                
                {mapData?.image_url && (
                    <ImageOverlay
                        url={mapData.image_url}
                        bounds={mapData.bounds}
                        opacity={0.8}
                    />
                )}
                
                {mapData?.geojson && (
                    <GeoJSON 
                        data={mapData.geojson}
                        style={{ color: '#ff0000', weight: 2 }}
                    />
                )}
            </MapContainer>
        </div>
    );
};

export default MapComponent;
