import React, { useState, useEffect } from 'react';
import MapComponent from './MapComponent';
import { getBoundary, getMapData } from './api';
import './styles.css';

function App() {
    const [boundary, setBoundary] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadBoundary = async () => {
            try {
                const response = await getBoundary();
                setBoundary(response.geojson);
            } catch (error) {
                console.error('Error loading boundary:', error);
            } finally {
                setLoading(false);
            }
        };
        loadBoundary();
    }, []);

    return (
        <div className="app">
            <h1>PEI Coastal Erosion Analysis</h1>
            {loading ? (
                <div className="loading">Loading PEI boundary...</div>
            ) : (
                <MapComponent boundary={boundary} />
            )}
        </div>
    );
}

export default App;
